import { Col, Row, Button } from "react-bootstrap";
import { useSelector, useDispatch } from "react-redux";

const Favourites = () => {
  const favouritesContent = useSelector(state => state.favourites.content);
  const dispatch = useDispatch();

  return (
    <Row>
      <Col sm={12}>
        <ul style={{ listStyle: "none" }}>
          {favouritesContent.map((job, i) => (
            <li key={i} className="my-4">
              <Button variant="danger" onClick={() => {dispatch({ type: "REMOVE_TO_FAV", payload: i });}}></Button>
              <img className="book-cover-small" src={job.imageUrl} alt="book selected" />
              {job.title}
            </li>
          ))}
        </ul>
      </Col>
      <Row>
        <Col sm={12} className="font-weight-bold mb-3 ml-4">
          TOTAL: {favouritesContent.reduce((acc, currentValue) => acc + parseFloat(currentValue.price), 0)}â‚¬
        </Col>
      </Row>
    </Row>
  );
};

export default Favourites;
